package Aula_12_for;

public class Aula_01_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*Usamos for quando sabemos exatamente a quantidade de repetições
		 que será efetuada
		 
		 Composição -> contador, condição e incrementador
		 
		 */
		for(int i=0;i<10;i++) {
			System.out.println(i);
		}
		
	}

}
